import React from 'react';

const Alert = ({ alert }) => {
	return (
		alert !== null && (
			<div style={containerStyle} className={`alert alert-${alert.type}`}>
				<h6>{alert.msg}</h6>
			</div>
		)
	);
};
const containerStyle = {
	textAlign: 'center',
	margin: 'auto 0',
	maxWidth: '250px',
};

export default Alert;
