import React, { Component, Fragment } from 'react';

import axios from 'axios';

class App extends Component {
	state = {
		city: 'boston',
		weather: {},
		loading: false,
	};

	//getting info from weather api
	async componentDidMount() {
		this.setState({ loading: true });
		const res = await axios.get(
			`http://api.openweathermap.org/data/2.5/weather?q=boston&appid=e9629992d0bde86eaddc6391f50171b7`,
		);
		this.setState({ weather: res.data });
		console.log(this.state.weather.main);
	}

	render() {
		const { name, main, weather, wind, loading } = this.state.weather;

		return (
			<Fragment>
				<div className='App'>
					<div className='container'>
						<h1>WorldWeather</h1>
						<h2>{name}</h2>
						<h3>
							<i>ICON</i>
						</h3>
						{/* <h3>{description}</h3> */}
					</div>
				</div>
			</Fragment>
		);
	}
}

export default App;
