import React, { Fragment } from 'react';

const Layout = (props) => {
	const {
		name,
		country,
		description,
		temp,
		feels_like,
		temp_max,
		temp_min,
		speed,
		deg,
		humidity,
		pressure,
		lat,
		lon,
	} = this.props;

	return (
		<Fragment>
			<div>
				<div className='container'>
					<h2>WorldWeather</h2>
					<h3>{`${name} , ${country}`}bla bla</h3>
					<i className={`wi ${icon} display-4 py-3`}></i>
					<h3 className='py-2'>{description}</h3>
					<h1>{`${temp}`}&deg;C</h1>
					<h4>{`${temp_min} | ${temp_max}`}</h4>
					<h6>{`Feels like: ${feels_like}`}&deg;C</h6>
					<h6>{`Wind: ${speed}m/s ${deg}`}&deg;</h6>
					<h6>{`Humidity: ${humidity}`}%</h6>
					<h6>{`Pressure: ${pressure} `}hPa</h6>
					<h6>
						{`Lat: ${lat}`}&deg;{` | Lon: ${lon}`}&deg;
					</h6>
				</div>
			</div>
		</Fragment>
	);
};

export default Layout;
