import React, { Component, Fragment } from 'react';
import axios from 'axios';

import 'weather-icons/css/weather-icons.css';

class GetWeather extends Component {
	constructor(props) {
		super(props);
		this.state = {
			city: 'Boston',
			weatherData: {},
			icon: '',
			loading: false,
		};
		this.weatherIcon = {
			Thunderstorm: 'wi-thunderstorm',
			Dizzle: 'wi-sleet',
			Rain: 'wi-storm-showers',
			Snow: 'wi-snow',
			Atmosphere: 'wi-fog',
			Clear: 'wi-day-sunny',
			Clouds: 'wi-day-fog',
		};
	}

	//getting info from weather api
	getWeather = async (e) => {
		e.preventdefault();
		this.setState({ city: e.target.elements.city.value });
		this.setState({ loading: true });
		const res = await axios.get(
			`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&units=metric&appid=e9629992d0bde86eaddc6391f50171b7`,
		);
		const result = res.data;
		console.log(result);

		const { name } = result;
		const { country } = result.sys;
		const { lat, lon } = result.coord;
		const {
			temp,
			temp_min,
			temp_max,
			feels_like,
			humidity,
			pressure,
		} = result.main;
		const { description, id } = result.weather[0];
		const { speed, deg } = result.wind;

		this.setState({
			weatherData: {
				name,
				country,
				id,
				description,
				temp: temp.toFixed(0),
				feels_like: feels_like.toFixed(0),
				temp_min: temp_min.toFixed(0),
				temp_max: temp_max.toFixed(0),
				speed,
				deg,
				humidity,
				pressure,
				lat: lat.toFixed(1),
				lon: lon.toFixed(1),
			},
		});
		this.getWeatherIcon(id);
	};
	getWeatherIcon(rangeId) {
		switch (true) {
			case rangeId >= 200 && rangeId <= 232:
				this.setState({ icon: 'wi-thunderstorm' });
				break;

			case rangeId >= 300 && rangeId <= 321:
				this.setState({ icon: 'wi-sleet' });
				break;

			case rangeId >= 500 && rangeId <= 531:
				this.setState({ icon: 'wi-storm-showers' });
				break;

			case rangeId >= 600 && rangeId <= 622:
				this.setState({ icon: 'wi-snow' });
				break;

			case rangeId >= 701 && rangeId <= 781:
				this.setState({ icon: 'wi-fog' });
				break;

			case rangeId === 800:
				this.setState({ icon: 'wi-day-sunny' });
				break;

			case rangeId >= 801 && rangeId <= 804:
				this.setState({ icon: 'wi-day-fog' });
				break;
			default:
				this.setState({ icon: 'wi-day-fog' });
		}
	}

	render() {
		const {
			name,
			country,
			description,
			temp,
			feels_like,
			temp_max,
			temp_min,

			speed,
			deg,
			humidity,
			pressure,
			lat,
			lon,
		} = this.state.weatherData;
		const { icon } = this.state;
		return (
			<Fragment>
				<div className='App'>
					<div className='container'>
						<h2>WorldWeather</h2>
						<Form loadweather={this.getWeather} />
						<h3>{`${name} , ${country}`}</h3>
						<i className={`wi ${icon} display-4 py-3`}></i>
						<h3 className='py-2'>{description}</h3>
						<h1>{`${temp}`}&deg;C</h1>
						<h4>{`${temp_min} | ${temp_max}`}</h4>
						<h6>{`Feels like: ${feels_like}`}&deg;C</h6>
						<h6>{`Wind: ${speed}m/s ${deg}`}&deg;</h6>
						<h6>{`Humidity: ${humidity}`}%</h6>
						<h6>{`Pressure: ${pressure} `}hPa</h6>
						<h6>
							{`Lat: ${lat}`}&deg;{` | Lon: ${lon}`}&deg;
						</h6>
					</div>
				</div>
			</Fragment>
		);
	}
}

export default GetWeather;
