import React, { Component, Fragment } from 'react';
import axios from 'axios';
import WeatherIcon from './WeatherIcon';

class GetWeather extends Component {
	constructor(props) {
		super(props);
		this.state = {
			city: 'Boston',
			weatherData: {},
			weatherIcon: '',
			loading: false,
		};
	}

	//getting info from weather api
	async componentDidMount() {
		this.setState({ loading: true });
		const res = await axios.get(
			`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=e9629992d0bde86eaddc6391f50171b7`,
		);
		const result = res.data;

		console.log(result);
		const { name } = result;
		const { country, sunrise, sunset } = result.sys;
		const { lat, lon } = result.coord;
		const {
			temp,
			temp_min,
			temp_max,
			feels_like,
			humidity,
			pressure,
		} = result.main;
		const { description, icon, icon_id } = result.weather[0];
		const { speed, deg } = result.wind;

		this.setState({
			weatherData: {
				name,
				country,
				sunrise,
				sunset,
				description,
				temp: Math.floor(temp - 273.15).toFixed(0),
				feels_like: Math.floor(feels_like - 273.15).toFixed(0),
				temp_min: Math.floor(temp_min - 273.15).toFixed(0),
				temp_max: Math.floor(temp_max - 273.15).toFixed(0),
				speed,
				deg,
				humidity,
				pressure,
				lat: lat.toFixed(1),
				lon: lon.toFixed(1),
			},
			weatherIcon: {
				icon,
				icon_id,
			},
		});
	}

	render() {
		return (
			<Fragment>
				<div className='App'>
					<div className='container'>
						<Layout weatherData={this.state.weatherData} />
					</div>
				</div>
			</Fragment>
		);
	}
}

export default GetWeather;
