import React, { Component, Fragment } from 'react';
import axios from 'axios';
import GetWeather from './components/GetWeather';

class App extends Component {
	constructor(props) {
		super(props);
		this.state = {
			city: 'Boston',
			weatherData: [],
			icon: '',
			loading: false,
		};
		this.weatherIcon = {
			Thunderstorm: 'wi-thunderstorm',
			Dizzle: 'wi-sleet',
			Rain: 'wi-storm-showers',
			Snow: 'wi-snow',
			Atmosphere: 'wi-fog',
			Clear: 'wi-day-sunny',
			Clouds: 'wi-day-fog',
		};
	}

	//getting info from weather api
	async componentDidMount() {
		this.setState({ loading: true });
		const res = await axios.get(
			`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&units=metric&appid=e9629992d0bde86eaddc6391f50171b7`,
		);
		this.setState({ weatherData: res.data });
		console.log(this.state.weatherData);
	}

	render() {
		return (
			<Fragment>
				<div className='App'>
					<div className='container'>
						<GetWeather weatherData={this.state.weatherData} />
					</div>
				</div>
			</Fragment>
		);
	}
}

export default App;
