import React, { Component, Fragment } from 'react';
import 'weather-icons/css/weather-icons.css';
import axios from 'axios';
import Layout from './Layout';
import Form from './Form';

class GetWeather extends Component {
	constructor(props) {
		super(props);
		this.state = {
			city: 'boston',
			weatherData: {},
			loading: false,
		};
		this.weatherIcon = {
			Thunderstorm: 'wi-thunderstorm',
			Dizzle: 'wi-sleet',
			Rain: 'wi-storm-showers',
			Snow: 'wi-snow',
			Atmosphere: 'wi-fog',
			Clear: 'wi-day-sunny',
			Clouds: 'wi-day-fog',
		};
	}

	//find a new location
	searchCity = async (text) => {
		this.setState({ city: text });
		const res = await axios.get(
			`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&units=metric&appid=e9629992d0bde86eaddc6391f50171b7`,
		);
		const result = res.data;
		console.log(result);

		const { name } = result;
		const { country } = result.sys;
		const { lat, lon } = result.coord;
		const {
			temp,
			temp_min,
			temp_max,
			feels_like,
			humidity,
			pressure,
		} = result.main;
		const { description, id } = result.weather[0];
		const { speed, deg } = result.wind;

		this.setState({
			weatherData: {
				name,
				country,
				id,
				description,
				temp: temp.toFixed(0),
				feels_like: feels_like.toFixed(0),
				temp_min: temp_min.toFixed(0),
				temp_max: temp_max.toFixed(0),
				speed,
				deg,
				humidity,
				pressure,
				lat: lat.toFixed(1),
				lon: lon.toFixed(1),
				icon: this.getWeatherIcon(id),
			},
		});
		this.getWeatherIcon(id);
	};
	getWeatherIcon(rangeId) {
		switch (true) {
			case rangeId >= 200 && rangeId <= 232:
				return 'wi-thunderstorm';

			case rangeId >= 300 && rangeId <= 321:
				return 'wi-sleet';

			case rangeId >= 500 && rangeId <= 531:
				return 'wi-storm-showers';

			case rangeId >= 600 && rangeId <= 622:
				return 'wi-snow';

			case rangeId >= 701 && rangeId <= 781:
				return 'wi-fog';

			case rangeId === 800:
				return 'wi-day-sunny';

			case rangeId >= 801 && rangeId <= 804:
				return 'wi-day-fog';

			default:
				return 'wi-day-fog';
		}
	}

	onChange = (e) => {
		this.setState({ city: e.target.value });
	};

	render() {
		return (
			<Fragment>
				<div className='App'>
					<h2>WorldWeather</h2>
					<div className='container' style={containerStyle}>
						<form onSubmit={this.searchCity} className='form'>
							<div className='input-group mb-2'>
								<input
									type='text'
									name='city'
									className='form-control'
									placeholder='Enter City...'
									aria-label="Recipient's username"
									aria-describedby='basic-addon2'
									value={this.state.city}
									onChange={this.onChange}
								/>
								<button
									type='submit'
									className='btn btn-outline-secondary input-group-append'
								>
									Find
								</button>
							</div>
						</form>
					</div>

					<Layout weatherData={this.state.weatherData} />
				</div>
			</Fragment>
		);
	}
}
const containerStyle = {
	textAlign: 'center',
	marginTop: '20px',
	maxWidth: '250px',
};
export default GetWeather;
