import React, { Component, Fragment } from 'react';
import 'weather-icons/css/weather-icons.css';
import axios from 'axios';
import Layout from './Layout';
import Form from './Form';
import PropTypes from 'prop-types';
import Alert from './Alert';

class GetWeather extends Component {
	constructor(props) {
		super(props);
		this.state = {
			weatherData: {},
			loading: false,
			alert: null,
		};
		this.weatherIcon = {
			Thunderstorm: 'wi-thunderstorm',
			Dizzle: 'wi-sleet',
			Rain: 'wi-storm-showers',
			Snow: 'wi-snow',
			Atmosphere: 'wi-fog',
			Clear: 'wi-day-sunny',
			Clouds: 'wi-day-fog',
		};
	}

	async componentDidMount() {
		const city = 'Boston';
		const res = await axios.get(
			`http://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=e9629992d0bde86eaddc6391f50171b7`,
		);
		const result = res.data;
		console.log(result);
		this.weatherDisplay(result);
	}

	searchUser = async (text) => {
		try {
			const res = await axios.get(
				`http://api.openweathermap.org/data/2.5/weather?q=${text}&units=metric&appid=e9629992d0bde86eaddc6391f50171b7`,
			);
			const result = res.data;
			console.log(result);

			this.weatherDisplay(result);
		} catch (error) {
			// Error ðŸ˜¨
			if (error.response) {
				/*
				 * The request was made and the server responded with a
				 * status code that falls out of the range of 2xx
				 */
				console.log(error.response.data);
				console.log(error.response.status);
				console.log(error.response.headers);
			} else if (error.request) {
				/*
				 * The request was made but no response was received, `error.request`
				 * is an instance of XMLHttpRequest in the browser and an instance
				 * of http.ClientRequest in Node.js
				 */
				console.log(error.request);
			} else {
				// Something happened in setting up the request and triggered an Error
				console.log('Error', error.message);
			}
			console.log(error);
		}
	};

	weatherDisplay = (result) => {
		const { name } = result;
		const { country } = result.sys;
		const { lat, lon } = result.coord;
		const {
			temp,
			temp_min,
			temp_max,
			feels_like,
			humidity,
			pressure,
		} = result.main;
		const { description, id } = result.weather[0];
		const { speed, deg } = result.wind;

		this.setState({
			weatherData: {
				name,
				country,
				id,
				description,
				temp: temp.toFixed(0),
				feels_like: feels_like.toFixed(0),
				temp_min: temp_min.toFixed(0),
				temp_max: temp_max.toFixed(0),
				speed,
				deg,
				humidity,
				pressure,
				lat: lat.toFixed(1),
				lon: lon.toFixed(1),
				icon: this.getWeatherIcon(id),
			},
		});
		this.getWeatherIcon(id);
	};

	getWeatherIcon(rangeId) {
		switch (true) {
			case rangeId >= 200 && rangeId <= 232:
				return 'wi-thunderstorm';

			case rangeId >= 300 && rangeId <= 321:
				return 'wi-sleet';

			case rangeId >= 500 && rangeId <= 531:
				return 'wi-storm-showers';

			case rangeId >= 600 && rangeId <= 622:
				return 'wi-snow';

			case rangeId >= 701 && rangeId <= 781:
				return 'wi-fog';

			case rangeId === 800:
				return 'wi-day-sunny';

			case rangeId >= 801 && rangeId <= 804:
				return 'wi-day-fog';

			default:
				return 'wi-day-fog';
		}
	}

	//set alert
	setAlert = (msg) => {
		this.setState({ alert: { msg } });
		setTimeout(() => this.setState({ alert: null }), 2000);
	};

	render() {
		return (
			<Fragment>
				<div className='App'>
					<h2>WorldWeather</h2>
					<Form searchUser={this.searchUser} setAlert={this.setAlert} />
					<Alert alert={this.state.alert} />
					<Layout weatherData={this.state.weatherData} />
				</div>
			</Fragment>
		);
	}
}

GetWeather.propType = {
	searchUser: PropTypes.func.isRequired,
	setAlert: PropTypes.func.isRequired,
};

export default GetWeather;
