import React, { Component, Fragment } from 'react';

import axios from 'axios';

class App extends Component {
	constructor(props) {
		super(props);
		this.state = {
			city: 'Boston',
			weather: {},
			loading: false,
		};
	}

	//getting info from weather api
	async componentDidMount() {
		this.setState({ loading: true });
		const res = await axios.get(
			`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=e9629992d0bde86eaddc6391f50171b7`,
		);

		console.log(res);
	}

	render() {
		const { name, timezone, main } = this.state.weather;
		return (
			<Fragment>
				<div className='App'>
					<div className='container'>
						<h1>WorldWeather</h1>
						<h2>{name}</h2>
						<h2>{timezone}</h2>
						<h2>{main}</h2>
					</div>
				</div>
			</Fragment>
		);
	}
}

export default App;
