import React, { Component } from 'react';
import WeatherIcons from 'react-weathericons';

class GetWeather extends Component {
	state = {
		city: 'Boston',
		icon: 'wi wi-day-lightning',
		description: 'sunny',
		temp: 20,
		minTemp: 15,
		maxTemp: 25,
	};

	render() {
		const { city, icon, description, temp, minTemp, maxTemp } = this.state;
		return (
			<div>
				<h1>Weather</h1>
				<h2>{city}</h2>
				<WeatherIcons name='cloud' />
				<i className={icon}></i>
				<h3>{description}</h3>
				<h2>{`${temp}\u00B0C`}</h2>
				<span>
					<h5>{`${minTemp}\u00B0C   ${maxTemp}\u00B0C`}</h5>
					<h5>{`Min | Max`}</h5>
				</span>
			</div>
		);
	}
}

export default GetWeather;
