import React, { Component, Fragment } from 'react';
import axios from 'axios';
import 'weather-icons/css/weather-icons.css';

class GetWeather extends Component {
	constructor(props) {
		super(props);
		this.state = {
			city: 'Boston',
			weatherData: {},
			icon: undefined,
			loading: false,
		};
		this.weatherIcon = {
			Thunderstorm: 'wi-thunderstorm',
			Dizzle: 'wi-sleet',
			Rain: 'wi-storm-showers',
			Snow: 'wi-snow',
			Atmosphere: 'wi-fog',
			Clear: 'wi-day-sunny',
			Clouds: 'wi-day-fog',
		};
	}

	//getting info from weather api
	async componentDidMount() {
		this.setState({ loading: true });
		const res = await axios.get(
			`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=e9629992d0bde86eaddc6391f50171b7`,
		);
		const result = res.data;
		console.log(result);

		const { name } = result;
		const { country, sunrise, sunset } = result.sys;
		const { lat, lon } = result.coord;
		const {
			temp,
			temp_min,
			temp_max,
			feels_like,
			humidity,
			pressure,
		} = result.main;
		const { description, id } = result.weather[0];
		const { speed, deg } = result.wind;

		this.setState({
			weatherData: {
				name,
				country,
				id,
				sunrise,
				sunset,
				description,
				temp: Math.floor(temp - 273.15).toFixed(0),
				feels_like: Math.floor(feels_like - 273.15).toFixed(0),
				temp_min: Math.floor(temp_min - 273.15).toFixed(0),
				temp_max: Math.floor(temp_max - 273.15).toFixed(0),
				speed,
				deg,
				humidity,
				pressure,
				lat: lat.toFixed(1),
				lon: lon.toFixed(1),
			},
		});
		this.getWeatherIcon(this.weatherIcon, id);
	}
	getWeatherIcon(icon, rangeId) {
		switch (true) {
			case rangeId >= 200 && rangeId <= 232:
				this.setState({ icon: this.weatherIcon.Thunderstorms });
				break;

			case rangeId >= 300 && rangeId <= 321:
				this.setState({ icon: this.weatherIcon.Drizzle });
				break;

			case rangeId >= 500 && rangeId <= 531:
				this.setState({ icon: this.weatherIcon.Rain });
				break;

			case rangeId >= 600 && rangeId <= 622:
				this.setState({ icon: this.weatherIcon.Snow });
				break;

			case rangeId >= 701 && rangeId <= 781:
				this.setState({ icon: this.weatherIcon.Atmosphere });
				break;

			case rangeId === 800:
				this.setState({ icon: this.weatherIcon.Clear });
				break;

			case rangeId >= 801 && rangeId <= 804:
				this.setState({ icon: this.weatherIcon.Clouds });
				break;
			default:
				this.setState({ icon: this.weatherIcon.Clouds });
		}
		console.log(icon);
	}

	render() {
		const {
			name,
			country,
			icon,
			description,
			temp,
			feels_like,
			temp_max,
			temp_min,
			sunrise,
			sunset,
			speed,
			deg,
			humidity,
			pressure,
			lat,
			lon,
		} = this.state.weatherData;
		return (
			<Fragment>
				<div className='App'>
					<div className='container'>
						<h1>WorldWeather</h1>
						<h2>{`${name} , ${country}`}</h2>
						<i className='wi wi-day-sunny display-4 py-3'></i>
						<h2 className='py-2'>{description}</h2>
					</div>
				</div>
			</Fragment>
		);
	}
}

export default GetWeather;
