import React, { Component, Fragment } from 'react';

import axios from 'axios';

class App extends Component {
	constructor(props) {
		super(props);
		this.state = {
			city: 'Boston',
			weatherData: {},
			loading: false,
		};
	}

	//getting info from weather api
	async componentDidMount() {
		this.setState({ loading: true });
		const res = await axios.get(
			`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=e9629992d0bde86eaddc6391f50171b7`,
		);
		const result = res.data;

		console.log(result);
		const { name } = result;
		const { country, sunrise, sunset } = result.sys;
		const { lat, lon } = result.coord;
		const {
			temp,
			temp_min,
			temp_max,
			feels_like,
			humidity,
			pressure,
		} = result.main;
		const { description, icon } = result.weather[0];
		const { speed, deg } = result.wind;

		this.setState({
			weatherData: {
				name,
				country,
				description,
				icon,
				temp: temp.toFixed(0),
				feels_like: feels_like.toFixed(0),
				temp_min: temp_min.toFixed(0),
				temp_max: temp_max.toFixed(0),
				speed,
				deg,
				humidity,
				pressure,
				lat: lat.toFixed(1),
				lon: lon.toFixed(1),
			},
		});
	}

	render() {
		const { name, country, temp } = this.state.weatherData;
		return (
			<Fragment>
				<div className='App'>
					<div className='container'>
						<h1>WorldWeather</h1>
						<h2>{name}</h2>
						<h2>{temp}</h2>
					</div>
				</div>
			</Fragment>
		);
	}
}

export default App;
